export type AnyDimensions =
  | { width: number; height: number; aspectRatio?: undefined }
  | { width: number; aspectRatio: number; height?: undefined }
  | { height: number; aspectRatio: number; width?: undefined };

export type SizeDimensions = { width: number; height: number };

export function sizeDimensions(dimensions: AnyDimensions): SizeDimensions {
  if (dimensions.aspectRatio !== undefined) {
    if (dimensions.width !== undefined) {
      return {
        width: dimensions.width,
        height: Math.ceil(dimensions.width * dimensions.aspectRatio),
      };
    }

    if (dimensions.height !== undefined) {
      return {
        height: dimensions.height,
        width: Math.ceil(dimensions.height * dimensions.aspectRatio),
      };
    }
  }

  return dimensions;
}

import superjson from "superjson";
import type { z } from "zod";

export type URLSerializeOptions<T> = {
  baseURL?: string;
  paramName?: string;
} & SerializeOptions<T>;

export type SerializeOptions<T> = {
  include?: (keyof T)[];
  exclude?: (keyof T)[];
};

export type StateSerializer<State> = {
  createURL(state: State, options?: URLSerializeOptions<State>): string;
  createString(state: State, options?: SerializeOptions<State>): string;
};

export const createStateSerializer = <
  TSchema extends z.ZodType,
  State extends z.infer<TSchema> = z.infer<TSchema>,
>(
  schema: TSchema,
): StateSerializer<State> => {
  const createString: StateSerializer<State>["createString"] = (
    state,
    options,
  ) => {
    let filteredState = { ...state };

    if (options?.include?.length) {
      filteredState = Object.fromEntries(
        Object.entries(filteredState).filter(([key]) =>
          options.include?.includes(key as keyof State),
        ),
      ) as State;
    }

    if (options?.exclude?.length) {
      filteredState = Object.fromEntries(
        Object.entries(filteredState).filter(
          ([key]) => !options.exclude?.includes(key as keyof State),
        ),
      ) as State;
    }

    const validated = schema.parse(filteredState);
    return superjson.stringify(validated);
  };

  return {
    createString,
    createURL: (state, options): string => {
      const paramName = options?.paramName || "state";
      const url = options?.baseURL || window.location.pathname;
      const parameters = new URLSearchParams(url);
      const [pathname] = url.split("?");
      const serialized = createString(state, options);
      parameters.append(paramName, serialized);
      return `${pathname}?${parameters.toString()}`;
    },
  };
};

export const createStateDeserializer = <
  TSchema extends z.ZodType,
  State extends z.infer<TSchema> = TSchema,
>(
  schema: TSchema,
) => ({
  fromString(
    stringifiedState: string | null | undefined,
    defaultValue?: State,
  ):
    | { error: Error; success: false; data?: undefined }
    | { error?: undefined; data: State; success: true } {
    try {
      if (stringifiedState === null) {
        if (defaultValue !== undefined) {
          return { data: defaultValue, error: undefined, success: true };
        }

        throw new Error("state is null");
      }
      if (stringifiedState === undefined) {
        if (defaultValue !== undefined) {
          return { data: defaultValue, error: undefined, success: true };
        }

        throw new Error("state is undefined");
      }

      const decoded = decodeURIComponent(stringifiedState);
      const parsed = superjson.parse(decoded);
      return schema.safeParse(parsed);
    } catch (error) {
      if (!(error instanceof Error)) {
        return { error: new Error(String(error)), success: false };
      }

      return { error, success: false };
    }
  },
});

import { create, type UseBoundStore, type StoreApi } from "zustand";
import {
	createContext,
	useContext,
	useRef,
	type PropsWithChildren,
} from "react";
import { useStore } from "zustand";

export function createDraftStore<T extends object>(parentStore: StoreApi<T>) {
	const state = parentStore.getState();
	console.log("Initial state:", state); // Let's see what we're starting with

	const store = create<T & { reset: () => void; push: () => void }>(
		(set, get) => {
			const initialState = {
				...state,
				reset: () => set(parentStore.getState()),
				push: () => parentStore.setState(get()),
			};
			console.log("Created store with:", initialState); // Verify store creation
			return initialState;
		},
	);

	// Let's verify the store has what we expect
	console.log("Final store state:", store.getState());
	return store;
}

export type CreateDraftStore<T> = T extends StoreApi<infer A>
	? UseBoundStore<StoreApi<DraftStore<A>>>
	: T extends { useRoot: () => StoreApi<infer A> }
	? UseBoundStore<StoreApi<DraftStore<A>>>
	: never;

export function useCreateDraftStore<T extends object>(
	// TODO: replace useRoot with a Symbol and look into how react determines use or change it to use__internal_root and hide the type
	anyParentStore: StoreApi<T> | { useRoot: () => StoreApi<T> },
) {
	const storeRef = useRef<UseBoundStore<StoreApi<DraftStore<T>>>>();
	const parentStore =
		"useRoot" in anyParentStore ? anyParentStore.useRoot() : anyParentStore;

	if (!storeRef.current) {
		storeRef.current = createDraftStore(parentStore);
	}

	return storeRef.current;
}

type Write<T, U> = Omit<T, keyof U> & U;
type DraftStore<T> = Write<T, { reset: () => void; push: () => void }>;

type UseStoreContext<TStore> = (<T = TStore>(
	selector?: (state: TStore) => T,
) => T) & { useRoot: () => StoreApi<TStore> };

export function createDraftStoreContext<TStore extends object>(
	parentStore: UseStoreContext<TStore>,
) {
	const StoreContext = createContext<StoreApi<DraftStore<TStore>> | null>(null);

	const Provider = (props: PropsWithChildren) => {
		const storeRef = useRef<StoreApi<DraftStore<TStore>>>();
		const parentRootStore = parentStore.useRoot();

		if (!storeRef.current) {
			storeRef.current = createDraftStore(parentRootStore);
		}

		return <StoreContext.Provider value={storeRef.current} {...props} />;
	};

	const useRoot = () => {
		const store = useContext(StoreContext);
		if (!store) {
			throw new Error("Store hook must be used within its Provider");
		}
		return store;
	};

	const useStoreContext = <T = DraftStore<TStore>>(
		selector: (state: DraftStore<TStore>) => T = (state) =>
			state as unknown as T,
	): T => useStore(useRoot(), selector);

	return [Provider, Object.assign(useStoreContext, { useRoot })] as const;
}

