import { create } from "zustand";
import { combine } from "zustand/middleware";
import { createStoreContext } from "./provider";

const [BearStoreProvider, useBearStore] = createStoreContext(
  ({ defaultBears: bears = 0 }: { defaultBears?: number }) =>
    create(
      combine({ bears }, (set, get) => ({
        addABear: () => set({ bears: get().bears + 1 }),
      })),
    ),
);

function Component() {
  return (
    <BearStoreProvider defaultBears={5}>
      <ChildComponent />
    </BearStoreProvider>
  );
}

function ChildComponent() {
  const bears = useBearStore((state) => state.bears);
  const addABear = useBearStore((state) => state.addABear);

  return (
    <div>
      <span>{bears}</span>
      <button type="button" onClick={addABear}>
        Add
      </button>
    </div>
  );
}

"use client";

import {
  createContext,
  useContext,
  useRef,
  type PropsWithChildren,
} from "react";
import { useStore, type StoreApi } from "zustand";

type ProviderProps<TParams> = PropsWithChildren<TParams>;

export function createStoreContext<
  TStore extends object,
  TParams = { store: StoreApi<TStore> },
>(
  createStore: (params: TParams) => StoreApi<TStore> = (({
    store,
  }: { store: StoreApi<TStore> }) => store) as unknown as (
    params: TParams,
  ) => StoreApi<TStore>,
) {
  const StoreContext = createContext<StoreApi<TStore> | null>(null);

  const Provider = ({ children, ...params }: ProviderProps<TParams>) => {
    const storeRef = useRef<StoreApi<TStore>>(createStore(params as TParams));

    return (
      <StoreContext.Provider value={storeRef.current}>
        {children}
      </StoreContext.Provider>
    );
  };

  const useRoot = () => {
    const store = useContext(StoreContext);
    if (!store) {
      throw new Error("Store hook must be used within its Provider");
    }
    return store;
  };

  const useStoreContext = <T = TStore>(
    selector: (state: TStore) => T = (state) => state as unknown as T,
  ): T => useStore(useRoot(), selector);

  return [Provider, Object.assign(useStoreContext, { useRoot })] as const;
}

import { create } from "zustand";
import { combine, persist } from "zustand/middleware";
import { createSuperJSONStorage } from "./superjson";

export const useBearStore = create(
  persist(
    combine({ bears: 0 }, (set, get) => ({
      addABear: () => set({ bears: get().bears + 1 }),
    })),
    {
      name: "food-storage", // name of the item in the storage (must be unique)
      storage: createSuperJSONStorage(() => sessionStorage), // (optional) by default, 'localStorage' is used
    },
  ),
);

import superjson from "superjson";
import type { PersistStorage } from "zustand/middleware";

export const createSuperJSONStorage = <T>(
  getStorage: () => Storage = () => localStorage,
): PersistStorage<T> => ({
  getItem: (name) => {
    const str = getStorage().getItem(name);
    if (!str) return null;
    return superjson.parse(str);
  },
  setItem: (name, value) =>
    getStorage().setItem(name, superjson.stringify(value)),
  removeItem: getStorage().removeItem,
});

import { create } from "zustand";
import { combine, persist } from "zustand/middleware";
import { createURLStorage } from "./url";
import { z } from "zod";

const BearsSchema = z.object({
  bears: z.number(),
});

export const useBearStore = create(
  persist(
    combine({ bears: 0 } satisfies z.infer<typeof BearsSchema>, (set, get) => ({
      addABear: () => set({ bears: get().bears + 1 }),
    })),
    {
      name: "food-storage", // name of the item in the storage (must be unique)
      storage: createURLStorage(BearsSchema), // zod schema
    },
  ),
);

import type { z } from "zod";
import type { PersistStorage } from "zustand/middleware";
import {
  createStateDeserializer,
  createStateSerializer,
  type SerializeOptions,
} from "../state-serialization";

export const createURLStorage = <T>(
  schema: z.ZodType<T>,
  options?: Omit<SerializeOptions<T>, "paramName">,
): PersistStorage<T> => {
  const { createString } = createStateSerializer(schema);
  const { fromString } = createStateDeserializer(schema);

  return {
    getItem(name) {
      if (typeof window === "undefined") return null;

      const params = new URLSearchParams(window.location.search);
      const param = params.get(name);

      if (!param) return null;

      const result = fromString(param);
      if (!result.success) return null;

      return { state: result.data };
    },

    setItem(name, value) {
      if (typeof window === "undefined") return;

      const url = new URL(window.location.href);
      const stringified = createString(value.state, options);
      url.searchParams.set(name, stringified);
      window.history.replaceState(null, "", url);
    },

    removeItem(name) {
      if (typeof window === "undefined") return;

      const url = new URL(window.location.href);
      url.searchParams.delete(name);
      window.history.replaceState(null, "", url.toString());
    },
  };
};

import { useRef } from "react";
import useDetectSticky from "./use-detect-sticky";

function Component() {
  const element = useRef<HTMLDivElement>(null);
  const hasScrolled = useDetectSticky(element);

  return (
    <div className="overflow-y-auto h-[500px]">
      {/* -top-px is important */}
      <div className="sticky -top-px" ref={element}>
        {hasScrolled && "You've scrolled"}
      </div>
    </div>
  );
}

import { useEffect, useState } from "react";

export default function useDetectSticky(
  ref: React.RefObject<HTMLElement | null>,
) {
  const [isSticky, setIsSticky] = useState(false);

  useEffect(() => {
    if (!ref.current) return;

    const cachedRef = ref.current;
    const observer = new IntersectionObserver(
      ([e]) => {
        if (!e) return;
        setIsSticky(e.intersectionRatio < 1);
      },
      { threshold: 1 },
    );

    observer.observe(cachedRef);

    return () => observer.unobserve(cachedRef);
  }, [ref.current]);

  return isSticky;
}

import { useRef } from "react";
import useScrollThreshold from "./use-scroll-threshold";

function Component() {
  const element = useRef<HTMLDivElement>(null);
  const hasScrolled = useScrollThreshold(100, element);

  return (
    <div className="overflow-y-auto h-[200px]" ref={element}>
      <div className="h-[500px] grid place-items-center">
        {hasScrolled && "You've scrolled"}
      </div>
    </div>
  );
}

import { type RefObject, useEffect, useState } from "react";

export default function useScrollThreshold(
  threshold: number,
  ref: RefObject<HTMLElement | null> | null,
) {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    if (ref && !ref.current) return;

    const updatePosition = () => {
      const scroll = ref ? (ref.current?.scrollTop ?? 0) : window.scrollY;
      setIsScrolled(scroll > threshold);
    };

    const element: GlobalEventHandlers = ref?.current ?? window;
    element.addEventListener("scroll", updatePosition);

    updatePosition();
    return () => element.removeEventListener("scroll", updatePosition);
  }, [threshold, ref]);

  return isScrolled;
}
